function($host = '', $engine = 'mysql')
  {
    if (!$host) {
      $arr = $this->o->fullOptions(self::getOptionId('dbs', $engine));
    }
    elseif (!bbn\Str::isUid($host)) {
      $host = $this->hostId($host, $engine);
    }

    if ($host) {
      $o   = &$this->o;
      $arr = array_map(
        function ($a) use ($o) {
          return $o->parent($a['id_parent']);
        },
        $this->o->getAliases($host)
      );
    }

    if (!empty($arr)) {
      $res = array_map(
        function ($a) {
          return [
            'id' => $a['id'],
            'text' => $a['text'],
            'name' => $a['code']
          ];
        },
        $arr
      );
      return $res;
    }

    return [];
  }

