        if (!($parent = $this->o->parent($this->o->getIdParent($host)))) {
          throw new \Exception(dgettext(X::tDom(), "Impossible to find the host engine"));
        }

        $engine = $parent['code'];
        $db     = $this->dbId($db, $engine);
      }
      else {
        $db = $this->dbId($db, $engine);
      }
    }

    if (bbn\Str::isUid($db)
        && ($id_parent = $this->o->fromCode('tables', $db))
        && ($id = $this->o->fromCode($table, $id_parent))
    ) {
      return $id;
    }

    return null;
  }

