importDb.code:
function($db, $host = '', $full = false)
  {
    $id_db = null;
    if (Str::isUid($host)
        && ($parent = $this->o->parent($this->o->getIdParent($host)))
        && ($id_dbs = self::getOptionId('dbs', $parent['code']))
    ) {
      if (!($id_db = $this->o->fromCode($db, $id_dbs))) {
        if ($id_db = $this->o->add(
          [
            'id_parent' => $id_dbs,
            'text' => $db,
            'code' => $db,
          ]
        )
        ) {
          $this->o->setCfg($id_db, ['allow_children' => 1, 'show_code' => 1]);
        }
      }

      if ($id_db) {
        if (!($id_procedures = $this->o->fromCode('procedures', $id_db))
            && ($id_procedures = $this->o->add(
              [
                'id_parent' => $id_db,
                'text' => dgettext(X::tDom(), 'Procedures'),
                'code' => 'procedures',
              ]
            ))
        ) {
          $this->o->setCfg(
            $id_procedures,
            [
              'show_code' => 1,
              'show_value' => 1,
              'allow_children' => 1
            ]
          );
        }

        if (!($id_connections = $this->o->fromCode('connections', $id_db))
            && ($id_connections = $this->o->add(
              [
                'id_parent' => $id_db,
                'text' => dgettext(X::tDom(), 'Connections'),
                'code' => 'connections',
              ]
            ))
        ) {
          $this->o->setCfg(
            $id_connections,
            [
              'show_alias' => 1,
              'notext' => 1,
              'id_root_alias' => self::getOptionId('connections'),
              'root_alias' => 'Connections'
            ]
          );
        }

        if (!($id_functions = $this->o->fromCode('functions', $id_db))
            && ($id_functions = $this->o->add(
              [
                'id_parent' => $id_db,
                'text' => dgettext(X::tDom(), 'Function'),
                'code' => 'functions',
              ]
            ))
        ) {
          $this->o->setCfg(
            $id_functions,
            [
              'show_code' => 1,
              'show_value' => 1,
              'allow_children' => 1
            ]
          );
        }

        if (!($id_tables = $this->o->fromCode('tables', $id_db))
            && ($id_tables = $this->o->add(
              [
                'id_parent' => $id_db,
                'text' => dgettext(X::tDom(), 'Tables'),
                'code' => 'tables',
              ]
            ))
        ) {
          $this->o->setCfg(
            $id_tables,
            [
              'show_code' => 1,
              'show_value' => 1,
              'allow_children' => 1
            ]
          );
        }

        if ($host) {
          $host_id = bbn\Str::isUid($host) ? $host : $this->hostId($host);
        }
        else {
          $host_id = $this->retrieveHost($id_db);
        }

        if ($host_id && $id_connections && $id_functions && $id_procedures && $id_tables) {
          if (!$this->db->count(
            'bbn_options', [
            'id_parent' => $id_connections,
            'id_alias' => $host_id
            ]
          )
          ) {
            $this->o->add(
              [
              'id_parent' => $id_connections,
              'id_alias' => $host_id
              ]
            );
          }

          if ($full) {
            if (!empty($host_id)) {
              try {
                $conn = $this->connection($host_id, $db);
              }
              catch (\Exception $e) {
                throw new \Exception(dgettext(X::tDom(), "Impossible to connect"));
              }

              $tables = $conn->getTables($db);
              if (!empty($tables)) {
                foreach ($tables as $t) {
                  $this->importTable($t, $id_db, $host_id);
                }
              }
            }
          }
        }
        else{
          throw new \Exception(dgettext(X::tDom(), "Impossible to find an host ID for DB")." ".$this->o->code($id_db));
        }
      }
    }

    return $id_db;
  }

retrieveHost.code:
function($id_db)
  {
    if ($this->check()
        && defined('BBN_DB_USER')
        && defined('BBN_DB_HOST')
        && ($connections = $this->o->fullOptions('connections', $id_db))
    ) {
      foreach ($connections as $c) {
        if ($c['alias']['code'] === BBN_DB_USER.'@'.BBN_DB_HOST) {
          return $c['alias']['id'];
        }
      }
    }

    return null;
  }

importTable.code:
function($table, $id_db, $host = '')
  {
    if (empty($host)) {
      $host_id = $this->retrieveHost($id_db);
    }
    else{
      $host_id = bbn\Str::isUid($host) ? $host : $this->hostId($host);
    }

    if ($host_id && ($id_tables = $this->o->fromCode('tables', $id_db))) {
      if (!($id_table = $this->o->fromCode($table, $id_tables))
          && ($id_table = $this->o->add(
            [
              'id_parent' => $id_tables,
              'text' => $table,
              'code' => $table,
            ]
          ))
      ) {
        $this->o->setCfg($id_table, ['allow_children' => 1]);
        if ($id_columns = $this->o->add(
          [
            'id_parent' => $id_table,
            'text' => dgettext(X::tDom(), "Columns"),
            'code' => 'columns'
          ]
        )
        ) {
          $this->o->setCfg(
            $id_columns,
            [
              'show_code' => 1,
              'show_value' => 1,
              'sortable' => 1
            ]
          );
        }

        if ($id_keys = $this->o->add(
          [
            'id_parent' => $id_table,
            'text' => dgettext(X::tDom(), "Keys"),
            'code' => 'keys',
          ]
        )
        ) {
          $this->o->setCfg(
            $id_keys,
            [
              'show_code' => 1,
              'show_value' => 1,
              'show_alias' => 1,
              'allow_children' => 1
            ]
          );
        }
      }
      else{
        $id_columns = $this->o->fromCode('columns', $id_table);
        $id_keys    = $this->o->fromCode('keys', $id_table);
      }

      $db = $this->o->code($id_db);
      if ($id_table
          && $id_columns
          && $id_keys
          && $db
          && ($conn = $this->connection($host_id, $db))
          && ($m = $conn->modelize($table))
          && !empty($m['fields'])
      ) {
        $num_cols     = 0;
        $num_cols_rem = 0;
        $fields       = [];
        $ocols        = array_flip($this->o->options($id_columns));
        foreach ($m['fields'] as $col => $cfg) {
          if ($opt_col = $this->o->option($col, $id_columns)) {
            $num_cols += (int)$this->o->set($opt_col['id'], bbn\X::mergeArrays($opt_col, $cfg));
          }
          elseif ($id = $this->o->add(
            bbn\X::mergeArrays(
              $cfg,
              [
                'id_parent' => $id_columns,
                'text' => $col,
                'code' => $col,
                'num' => $cfg['position']
              ]
            )
          )
          ) {
            $num_cols++;
            $opt_col       = $cfg;
            $opt_col['id'] = $id;
          }

          if ($opt_col) {
            $fields[$col] = $opt_col['id'];
          }

          if (isset($ocols[$col])) {
            unset($ocols[$col]);
          }
        }

        if (!empty($ocols)) {
          foreach ($ocols as $col => $id) {
            if (bbn\Str::isUid($id)) {
              $num_cols_rem += (int)$this->o->remove($id);
            }
          }
        }

        $num_keys     = 0;
        $num_keys_rem = 0;
        $okeys        = array_flip($this->o->options($id_keys));
        foreach ($m['keys'] as $key => $cfg) {
          $cols = $cfg['columns'] ?? [];
          unset($cfg['columns']);
          if (isset($cfg['ref_db'], $cfg['ref_table'], $cfg['ref_column'])
              && ($id_alias = $this->columnId($cfg['ref_column'], $cfg['ref_table'], $cfg['ref_db']))
          ) {
            $cfg['id_alias'] = $id_alias;
            unset($cfg['ref_db'], $cfg['ref_table'], $cfg['ref_column']);
          }

          if ($opt_key = $this->o->option($key, $id_keys)) {
            $num_keys += (int)$this->o->set($opt_key['id'], bbn\X::mergeArrays($opt_key, $cfg));
          }
          elseif ($id = $this->o->add(
            bbn\X::mergeArrays(
              $cfg, [
              'id_parent' => $id_keys,
              'text' => $key,
              'code' => $key
              ]
            )
          )
          ) {
            $this->o->setCfg(
              $id, [
              'show_code' => 1,
              'show_alias' => 1
              ]
            );
            $num_keys++;
            $opt_key       = $cfg;
            $opt_key['id'] = $id;
          }

          if (isset($okeys[$key])) {
            unset($okeys[$key]);
          }

          if ($opt_key && $cols) {
            foreach ($cols as $col){
              if (isset($fields[$col])) {
                if ($opt = $this->o->option($col, $opt_key['id'])) {
                  $this->o->set(
                    $opt['id'], bbn\X::mergeArrays(
                      $opt, [
                      'id_alias' => $fields[$col]
                      ]
                    )
                  );
                }
                else{
                  $tmp = [
                    'id_parent' => $opt_key['id'],
                    'id_alias' => $fields[$col],
                    'code' => $col,
                    'text' => $col
                  ];
                  if ($this->o->add($tmp)) {
                    $opt = $tmp;
                  }
                }
              }
            }
          }
        }

        if (!empty($okeys)) {
          foreach (array_values($okeys) as $id) {
            if (bbn\Str::isUid($id)) {
              $children = $this->o->items($id);
              foreach ($children as $cid) {
                $num_keys_rem += (int)$this->o->removeFull($cid);
              }

              $num_keys_rem += (int)$this->o->remove($id);
            }
          }
        }

        return [
          'columns' => $num_cols,
          'keys' => $num_keys,
          'columns_removed' => $num_cols_rem,
          'keys_removed' => $num_keys_rem
        ];
      }
    }

    return null;
  }

