importAll.code:
function($db = '')
  {
    $res = null;
    if ($tables = $this->db->getTables($db)) {
      $res = [
        'tables' => 0,
        'columns' => 0,
        'keys' => 0
      ];
      foreach ($tables as $t){
        if ($tmp = $this->import(($db ?: $this->db->getCurrent()).'.'.$t)) {
          $res['tables']++;
          $res['columns'] += $tmp['columns'];
          $res['keys']    += $tmp['keys'];
        }
      }
    }

    return $res;
  }

