function($id)
  {
    if (\bbn\Str::isUid($id)) {
      $cf    =& $this->class_cfg;
      $media = $this->getMedia($id, true);
      $fs    = new bbn\File\System();

      if ($media
          && ($path = dirname($media['file']))
          && is_file($media['file'])
          && $this->db->delete($cf['table'], [$cf['arch']['medias']['id'] => $id])
      ) {
        if ($fs->delete($path, false)) {
          bbn\X::cleanStoragePath($path);
        }

        return true;
      }
    }

    return false;
  }

