function($st, $maxlength = 50, $extension = NULL, $is_path = false)
  {
    $st      = self::removeAccents(self::cast($st));
    $allowed = '~\-_.,\(\[\)\]';

    // Arguments order doesn't matter
    $args = \func_get_args();
    foreach ($args as $i => $a){
      if ($i > 0) {
        if (\is_string($a)) {
          $extension = $a;
        }
        elseif (\is_int($a)) {
          $maxlength = $a;
        }
        elseif (\is_bool($a)) {
          $is_path = $a;
        }
      }
    }

    if (!\is_int($maxlength)) {
      $maxlength = mb_strlen($st);
    }

    if ($is_path) {
      $allowed .= '/';
    }

    if ($extension
        && (self::fileExt($st) === self::changeCase($extension, 'lower'))
    ) {
      $st = substr($st, 0, -(\strlen($extension) + 1));
    }
    elseif ($extension = self::fileExt($st)) {
      $st = substr($st, 0, -(\strlen($extension) + 1));
    }

    $st  = mb_ereg_replace("([^\w\s\d".$allowed.".])", '', $st);
    $st  = mb_ereg_replace("([\.]{2,})", '', $st);
    $res = mb_substr($st, 0, $maxlength);
    if ($extension) {
      $res .= '.' . $extension;
    }

    return $res;
  }

