          $st .= self::export($v, $remove_empty, $lev + 1);
        }
        elseif ($v === 0) {
          $st .= '0';
        }
        elseif (null === $v) {
          $st .= 'null';
        }
        elseif (\is_bool($v)) {
          $st .= $v === false ? 'false' : 'true';
        }
        elseif (\is_int($v) || \is_float($v)) {
          $st .= $v;
        }
        elseif (is_string($v)) {
          if (self::isBuid($v)) {
            $st .= '0x'.bin2hex($v);
          }
          elseif (!$remove_empty || !empty($v)) {
            $st .= '"'.self::escapeDquote($v).'"';
          }
        }
        else {
          try{
            $cls = get_class($v);
          }
          catch (\Exception $e){
            $st .= '"Unknown"';
          }

          if ($cls) {
            if ($cls === 'stdClass') {
              $st .= self::export($v, $remove_empty, $lev + 1);
            }
            else{
              $st .= 'Object '.$cls;
            }
          }
        }

        $st .= ','.PHP_EOL;
      }

      $st .= str_repeat($space, $lev - 1);
      $st .= $is_assoc ? '}' : ']';
      //$st .= \is_object($o) ? '}' : ']';
    }

    return $st;
  }

