function($bytes, $unit = 'B', $stop = false)
  {
    // pretty printer for byte values
    //
    $i     = 0;
    $units = ['', 'K', 'M', 'G', 'T'];
    while ($stop || ($bytes > 2000)){
      $i++;
      $bytes /= 1024;
      if ($stop === $units[$i]) {
        break;
      }
    }

    $st = $unit === 'B' ? "%d %s" : "%5.2f %s";
    return sprintf($st.$unit, $bytes, $units[$i]);
  }

