function($size, $unit_orig = 'B', $unit_dest = 'MB')
  {
    if (strlen($unit_orig) <= 1) {
      $unit_orig .= 'B';
    }

    if (strlen($unit_dest) <= 1) {
      $unit_dest .= 'B';
    }

    $base   = log($size) / log(1024);
    $suffix = array("", "KB", "MB", "GB", "TB");
    $f_base = floor($base);
    return round(pow(1024, $base - floor($base)), 1) . $suffix[$f_base];
  }

