function(array $arr)
  {
    $res = [];
    if (isset($arr[0])) {
      foreach ($arr as $a){
        if (isset($a['id']) && $this->has($a['id'])) {
          $res[] = $a;
        }
      }
    }
    elseif (isset($arr['items'])) {
      $res = $arr;
      unset($res['items']);
      foreach ($arr['items'] as $a){
        if (isset($a['id']) && $this->has($a['id'])) {
          if (!isset($res['items'])) {
            $res['items'] = [];
          }

          $res['items'][] = $a;
        }
      }
    }

    return $res;
  }

optionToPermission.code:
function($id_option)
  {
    if (bbn\Str::isUid($id_option)) {
      $aliases = $this->opt->getAliasItems($id_option);
      $root    = self::getOptionId('options');
      $id_perm = null;
      $all     = [];
      foreach ($aliases as $a) {
        $parents = $this->opt->parents($a);
        $all[]   = $parents;
        if (in_array($root, $parents)) {
          $id_perm = $a;
          break;
        }
      }

      if (!$id_perm) {
        foreach ($aliases as $i => $a) {
          foreach ($all[$i] as $b) {
            if ($this->opt->alias($b) === $root) {
              $id_perm = $a;
              break;
            }
          }
        }
      }

      return $id_perm ?: null;
    }
  }

