reopen_mbox.code:
function($mbox)
  {
      if ( \in_array($mbox, $this->get_all_names_mboxes()) ){
        return imap_reopen($this->stream, $this->mbParam . $mbox);
      }
      else {
        return imap_reopen($this->stream, $mbox);
      }
    }
    return false;
  }

sort_mbox.code:
function($criteria, $reverse = 0)
  {
      return imap_sort($this->stream, constant(strtoupper($criteria)), $reverse, SE_NOPREFETCH);
    }
    return false;
  }

get_msg_header.code:
function($msgnum, $uid = false)
  {
      if ( $uid ){
        return imap_fetchheader($this->stream, $msgnum, FT_UID);
      }
      return imap_fetchheader($this->stream, $msgnum);
    }
    return false;
  }

move_msg.code:
function($uid, $tombox)
  {
      return imap_mail_move($this->stream, $uid, $tombox, CP_UID);
    }
    return false;
  }

get_msg_body.code:
function($msgno, $part)
  {
      if ( empty($part) ){
        return imap_body($this->stream, $msgno);
      }
      return imap_fetchbody($this->stream, $msgno, $part);
    }
    return false;
  }

set_msg_flag.code:
function($seq, $flg, $remove = false)
  {
      return $remove ? imap_clearflag_full($this->stream, $seq, $flg) : imap_setflag_full($this->stream, $seq, $flg);
    }
    return false;
  }

