function()
  {
    if (
      $this->check() &&
      ($id = $this->db->select_one('bbn_emailings', 'id', [
        ['state', 'LIKE', 'ready'],
        ['sent', '<', date('Y-m-d H:i:s')],
        ['sent', 'isnotnull']
      ], ['sent' => 'ASC']))
    ){
      return $this->get_mailing($id);
    }
