get_all.code:
function($id_type = NULL, $simple = true)
  {
      $id_type = self::get_option_id($id_type);
    }
    $all = $this->db->get_column_values('bbn_notes_masks', 'id_note', $id_type ? [
      'id_type' => $id_type
    ] : []);
    $r = [];
    foreach ( $all as $a ){
      $r[] = $this->get($a, $simple);
    }
    return $r;
  }

get_text_value.code:
function($id_type, $fulltext = false)
  {
      $id_type = self::get_option_id($id_type);
    }
    $all = $this->get_all($id_type);
    $admin = new bbn\user\manager(\bbn\user::get_instance());
    if ( \is_array($all) ){
      $res = [];
      foreach ( $all as $a ){
        $tmp = [
          'text' => $a['name'],
          'value' => $a['id_note']
        ];
        if ( $fulltext ){
          $tmp['fulltext'] = $a['title'].
            ($a['default'] ? ' ('._('default').')' : '').
            ' - v'.$a['version'].' '.
            \bbn\date::format($a['creation']).' '._('by').' '.
            $admin->get_name($a['id_user']);
        }
        $res[] = $tmp;
      }
      return $res;
    }
    return null;
  }

