set_default.code:
function($id_note)
  {
    if ( $current ){
      if ( $old = $this->get_default($current['id_type']) ){
        if ( $old['id_note'] === $id_note ){
          return 1;
        }
        $this->db->update('bbn_notes_masks', [
          'def' => 0
        ], [
          'id_note' => $old['id_note']
        ]);
      }
      return $this->db->update('bbn_notes_masks', [
        'def' => 1
      ], [
        'id_note' => $id_note
      ]);
    }
  }

insert.code:
function($name, $id_type, $title, $content)
  {
    if ( !bbn\str::is_uid($id_type) ){
      $id_type = self::get_option_id($id_type);
    }
    if (
      $this->o->exists($id_type) &&
      ($id_note = $this->notes->insert($title, $content, $this->o->from_code('masks', 'types', 'notes', 'appui')))
    ){
      $data = [
        'id_note' => $id_note,
        'id_type' => $id_type,
        'name' => $name
      ];
      if ( !$this->count($id_type) ){
        $data['def'] = 1;
      }
      if ( $this->db->insert('bbn_notes_masks', $data) ){
        return $id_note;
      }
    }
    return null;
  }

update.code:
function(array $cfg)
  {
      !empty($cfg['id_note']) && 
      !empty($cfg['title']) &&
      !empty($cfg['content']) &&
      !empty($cfg['name'])
    ){
      $data = [
        'name' => $cfg['name'],
        'def' => !empty($cfg['def']) || !empty($cfg['default']) ? 1 : 0
      ];
      if ( !empty($cfg['id_type']) ){
        $data['id_type'] = $cfg['id_type'];
      }      
      $update_mask = $this->db->update('bbn_notes_masks', $data, ['id_note' => $cfg['id_note']]);
      $update_notes = $this->notes->update($cfg['id_note'], $cfg['title'], $cfg['content']);
      if ( $update_mask || $update_notes ){
        return true;
      }
    }
    return false;
  }

