__construct.code:
function(bbn\db $db)
  {
    parent::__construct($db);
    self::optional_init();
    $this->notes = new notes($this->db);
    $this->o = bbn\appui\options::get_instance();
  }

get.code:
function($id, $simple = false)
  {
    if (($mask = $this->db->rselect('bbn_notes_masks', [], ['id_note' => $id]))
        && ($data = $this->notes->get($mask['id_note'], null, $simple))
    ) {
      $data['default'] = $mask['def'];
      $data['id_type'] = $mask['id_type'];
      $data['type'] = $this->o->text($mask['id_type']);
      $data['name'] = $mask['name'];
      return $data;
    }
    return null;
  }

get_all.code:
function($id_type = NULL, $simple = true)
  {
    if (!empty($id_type) && !bbn\str::is_uid($id_type)) {
      $id_type = self::get_option_id($id_type);
    }
    $r = [];
    $all = $this->db->get_column_values(
      'bbn_notes_masks', 'id_note', $id_type ? [
      'id_type' => $id_type
      ] : []
    );
    foreach ($all as $a){
      if ($tmp = $this->get($a, $simple)) {
        $r[] = $tmp;
      }
    }
    return $r;
  }

get_text_value.code:
function($id_type, $fulltext = false)
  {
    if (!bbn\str::is_uid($id_type)) {
      $id_type = self::get_option_id($id_type);
    }
    if (bbn\str::is_uid($id_type)) {
      $all = $this->get_all($id_type);
      $admin = new bbn\user\manager(\bbn\user::get_instance());
      if (\is_array($all)) {
        $res = [];
        foreach ($all as $a) {
          $tmp = [
            'text' => $a['name'],
            'value' => $a['id_note']
          ];
          if ($fulltext) {
            $tmp['fulltext'] = $a['title'].
              ($a['default'] ? ' ('._('default').')' : '').
              ' - v'.$a['version'].' '.
              \bbn\date::format($a['creation']).' '._('by').' '.
              $admin->get_name($a['id_user']);
          }
          $res[] = $tmp;
        }
        return $res;
      }
    }
    return null;
  }

get_default.code:
function($id_type)
  {
    if (!bbn\str::is_uid($id_type)) {
      $id_type = self::get_option_id($id_type);
    }
    if ($id_note = $this->db->select_one(
      'bbn_notes_masks', 'id_note', [
      'id_type' => $id_type,
      'def' => 1
      ]
    ) 
    ) {
      return $this->get($id_note);
    }
    return null;
  }

set_default.code:
function($id_note)
  {
    $current = $this->get($id_note);
    if ($current) {
      if ($old = $this->get_default($current['id_type'])) {
        if ($old['id_note'] === $id_note) {
          return 1;
        }
        $this->db->update(
          'bbn_notes_masks', [
          'def' => 0
          ], [
          'id_note' => $old['id_note']
          ]
        );
      }
      return $this->db->update(
        'bbn_notes_masks', [
        'def' => 1
        ], [
        'id_note' => $id_note
        ]
      );
    }
  }

insert.code:
function($name, $id_type, $title, $content)
  {
    if (!bbn\str::is_uid($id_type)) {
      $id_type = self::get_option_id($id_type);
    }
    if ($this->o->exists($id_type) 
        && ($id_note = $this->notes->insert($title, $content, $this->o->from_code('masks', 'types', 'note', 'appui')))
    ) {
      $data = [
        'id_note' => $id_note,
        'id_type' => $id_type,
        'name' => $name
      ];
      if (!$this->count($id_type)) {
        $data['def'] = 1;
      }
      if ($this->db->insert('bbn_notes_masks', $data)) {
        return $id_note;
      }
    }
    return null;
  }

update.code:
function(array $cfg)
  {
    if (!empty($cfg['id_note'])  
        && !empty($cfg['title']) 
        && !empty($cfg['content']) 
        && !empty($cfg['name'])
    ) {
      $data = [
        'name' => $cfg['name'],
        'def' => !empty($cfg['def']) || !empty($cfg['default']) ? 1 : 0
      ];
      if (!empty($cfg['id_type'])) {
        $data['id_type'] = $cfg['id_type'];
      }      
      $update_mask = $this->db->update('bbn_notes_masks', $data, ['id_note' => $cfg['id_note']]);
      $update_notes = $this->notes->update($cfg['id_note'], $cfg['title'], $cfg['content']);
      if ($update_mask || $update_notes) {
        return true;
      }
    }
    return false;
  }

render.code:
function($note, $data)
  {
    if (bbn\str::is_uid($note)) {
      $note = $this->get($note);
    }
    if (!empty($note['content'])) {
      return \bbn\tpl::render($note['content'], $data);
    }
    return null;
  }

