option.code:
function($code = NULL)
  {
    if (bbn\Str::isUid($id = $this->fromCode(\func_get_args()))
        && ($opt = $this->nativeOption($id))
    ) {
      $this->_set_value($opt);
      $c =& $this->class_cfg['arch']['options'];
      if (bbn\Str::isUid($opt[$c['id_alias']]) && ($opt['alias'] = $this->nativeOption($opt[$c['id_alias']]))) {
        if ($opt[$c['id_alias']] === $id) {
          throw new \Exception(_("Impossible to have the same ID as ALIAS, check out ID").' '.$id);
        }
        else {
          $this->_set_value($opt['alias']);
        }
      }

      return $opt;
    }

    return null;
  }

options.code:
function($code = NULL)
  {
    if (bbn\Str::isUid($id = $this->fromCode(\func_get_args()))) {
      //var_dump("MY ID: $id");
      if ($r = $this->cacheGet($id, __FUNCTION__)) {
        return $r;
      }

      $opt = $this->db->rselectAll(
        $this->class_cfg['table'],
        [$this->class_cfg['arch']['options']['id'], $this->class_cfg['arch']['options']['text']],
        [$this->class_cfg['arch']['options']['id_parent'] => $id],
        [$this->class_cfg['arch']['options']['text'] => 'ASC']
      );
      $res = [];
      foreach ($opt as $r){
        $res[$r[$this->class_cfg['arch']['options']['id']]] = $r[$this->class_cfg['arch']['options']['text']];
      }

      $this->cacheSet($id, __FUNCTION__, $res);
      return $res;
    }

    return null;
  }

