function($code = NULL)
  {
    if (bbn\Str::isUid($id = $this->fromCode(\func_get_args()))) {
      $list = $this->items($id);
      if (\is_array($list)) {
        $res = [];
        foreach ($list as $i){
          if ($tmp = $this->option($i)) {
            $res[] = $tmp;
          }
          else {
            throw new \Exception(_("Impossible to find the ID").' '.$i);
          }
        }

        return $res;
      }
    }

    return null;
  }

