is_parent.code:
function($id, $id_parent)
  {
    // Preventing infinite loop
    $done = [$id];
    if (bbn\Str::isUid($id, $id_parent)) {
      while ($id = $this->getIdParent($id)){
        if ($id === $id_parent) {
          return true;
        }

        if (\in_array($id, $done, true)) {
          break;
        }

        $done[] = $id;
      }
    }

    return false;
  }

