add.code:
function(array $it, $force = false, $return_num = false)
  {
    if ($this->check()) {
      $res   = null;
      $items = !empty($it['items']) && \is_array($it['items']) ? $it['items'] : false;
      $id    = null;
      if ($this->_prepare($it)) {
        $c =& $this->class_cfg['arch']['options'];
        if ($it[$c['code']]) {
          $id = $this->db->selectOne(
            $this->class_cfg['table'],
            $c['id'],
            [
              $c['id_parent'] => $it[$c['id_parent']],
              $c['code'] => $it[$c['code']]
            ]
          );
        }
        elseif (!empty($it[$c['id']])) {
          $id = $this->db->selectOne(
            $this->class_cfg['table'],
            $c['id'],
            [
              $c['id'] => $it[$c['id']],
              $c['code'] => null
            ]
          );
        }

        if ($id
            && $force
            && (null !== $it[$c['code']])
        ) {
          $res = $this->db->update(
            $this->class_cfg['table'],
            [
              $c['text'] => $it[$c['text']],
              $c['id_alias'] => $it[$c['id_alias']],
              $c['value'] => $it[$c['value']],
              $c['num'] => $it[$c['num']] ?? null,
              $c['cfg'] => $it[$c['cfg']] ?? null
            ],
            [$c['id'] => $id]
          );
        }

        $values = [
          $c['id_parent'] => $it[$c['id_parent']],
          $c['text'] => $it[$c['text']],
          $c['code'] => empty($it[$c['code']]) ? null : $it[$c['code']],
          $c['id_alias'] => $it[$c['id_alias']],
          $c['value'] => $it[$c['value']],
          $c['num'] => $it[$c['num']] ?? null,
          $c['cfg'] => $it[$c['cfg']] ?? null
        ];
        if (!empty($it['id'])) {
          $values['id'] = $it['id'];
        }

        if (!$id
            && ($res = $this->db->insert($this->class_cfg['table'], $values))
        ) {
          $id = $this->db->lastId();
        }

        if ($res) {
          $this->deleteCache($id);
        }

        if ($items && bbn\Str::isUid($id)) {
          foreach ($items as $item){
            $item['id_parent'] = $id;
            $tmp               = (int)$this->add($item, $force, $return_num);
            if ($return_num && $tmp) {
              $res += $tmp;
            }
          }
        }
      }

      return $return_num ? $res : $id;
    }

    return null;
  }

