merge.code:
function($id, array $data, array $cfg = NULL)
  {
    if ($this->check()
        && ($o = $this->option($id))
    ) {
      if (!empty($data)) {
        $data = array_merge($o, $data);
        $c    =& $this->class_cfg['arch']['options'];
        $this->_prepare($data);
        if (isset($data[$c['id']])) {
          unset($data[$c['id']]);
        }
      }

      if ($cfg) {
        $ocfg = $this->getRawCfg($id);
        $data['cfg'] = json_encode(array_merge($ocfg ? json_decode($ocfg, true) : [], $cfg));
      }

      // id_parent cannot be edited this way
      if ($res = $this->db->update(
        $this->class_cfg['table'],
        $data,
        [$c['id'] => $id]
      )
      ) {
        $this->deleteCache($id);
        return $res;
      }

      return 0;
    }

    return null;
  }

