analyze_out.code:
function(array $options, array &$results = [])
  {
    if ($this->check()) {
      if (empty($results)) {
        $results['options'] = [];
        $results['ids']     = [];
        $results['aliases'] = [];
      }

      if (!empty($options['id'])) {
        $results['ids'][$options['id']] = null;
      }

      if (!empty($options['id_alias'])) {
        $results['aliases'][$options['id_alias']] = [
          'id' => null,
          'codes' => $this->getCodePath($options['id_alias'])
        ];
      }

      $items = false;
      if (!empty($options['items'])) {
        $items = $options['items'];
        unset($options['items']);
      }

      $results['options'][] = $options;
      if ($items) {
        foreach ($items as $it) {
          $this->analyzeOut($it, $results);
        }
      }

      return $results;
    }

    return null;
  }

export.code:
function($id, $deep = false, $return = false, $aliases = false)
  {
    if (($ret = $deep ? $this->rawTree($id) : $this->rawOptions($id))) {
      $ret  = $this->analyzeOut($ret);
      $res  = [];
      $done = [];
      $max  = 3;
      foreach ($ret['options'] as $i => $o) {
        if (!$i || in_array($o['id_parent'], $done, true)) {
          if (empty($o['id_alias'])) {
            $res[]  = $o;
            $done[] = $o['id'];
          }
        }
      }

      while ($max && (count($res) < count($ret['options']))) {
        foreach ($ret['options'] as $i => $o) {
          if (!empty($o['id_alias'])
              && !in_array($o['id'], $done, true)
              && in_array($o['id_parent'], $done, true)
              && in_array($o['id_alias'], $done, true)
          ) {
            $res[]  = $o;
            $done[] = $o['id'];
          }
        }

        $max--;
      }

      if (count($res) < count($ret['options'])) {
        foreach ($ret['options'] as $i => $o) {
          if (!in_array($o['id_parent'], $done, true)) {
            $o['id_parent'] = $this->getCodePath($o['id_parent']);
          }

          if (!empty($o['id_alias'])
              && !in_array($o['id'], $done, true)
          ) {
            if (!in_array($o['id_alias'], $done, true)) {
              $code_path     = $this->getCodePath($o['id_alias']);
              $o['id_alias'] = $code_path ?: $o['id_alias'];
            }

            $res[]  = $o;
            $done[] = $o['id'];
          }
        }

        $max--;
      }

      return $return ? $res : var_export($res, 1);
    }

    return null;
  }

import.code:
function(array $options, $id_parent = NULL, $return_num = false)
  {
    if ($this->check()) {
      $ids     = [];
      $num     = 0;
      $root_id = false;
      foreach ($options as $i => $o) {
        if (!$i) {
          $o['id_parent'] = $id_parent ?: $this->default;
          if ($o['id_alias']) {
            $o['id_alias'] = is_array($o['id_alias']) ? $this->fromCode(...$o['id_alias']) : null;
          }
        }
        elseif ($root_id) {
          if (!isset($ids[$o['id_parent']])) {
            $this->removeFull($root_id);
            throw new \Exception(_("Error while importing: no parent"));
          }

          $o['id_parent'] = $ids[$o['id_parent']];
          if (!empty($o['id_alias'])) {
            if (is_array($o['id_alias'])) {
              $o['id_alias'] = $this->fromCode(...$o['id_alias']);
            }
            elseif (isset($ids[$o['id_alias']])) {
              $o['id_alias'] = $ids[$o['id_alias']];
            }
            elseif (!$this->option($o['id_alias'])) {
              $o['id_alias'] = null;
            }
          }
        }

        if ($id = $this->add($o, true)) {
          if (!$i) {
            $root_id = $id;
          }

          $ids[$o['id']] = $id;
          $num++;
        }
        elseif ($i) {
          $this->removeFull($root_id);
          throw new \Exception(_("Error while importing: impossible to add"));
        }
      }

      return $return_num ? $num : $id;
    }

    return null;
  }

