function(bbn\Db $db, $id = NULL)
  {
    parent::__construct($db);
    $this->options = bbn\Appui\Options::getInstance();
    $this->fs      = new \bbn\File\System();
    if (\bbn\Str::isUid($id)) {
      $this->id = $id;
    }
    elseif (\is_string($id)) {
      $this->id = $this->options->fromCode($id, 'project', 'appui');
    }
    elseif (defined('BBN_APP_NAME')) {
      $this->id = $this->options->fromCode(BBN_APP_NAME, 'project', 'appui');
    }

    if (!empty($this->id)) {
      $this->setProjectInfo($this->id);
    }
  }

get_environment.code:
function($app_name = NULL)
  {
    $file_environment = \bbn\Mvc::getAppPath().'cfg/environment';
    if ($this->fs->isFile($file_environment.'.json')) {
      $envs = \json_decode($this->fs->getContents($file_environment.'.json'), true)[0];
    }
    elseif ($this->fs->isFile($file_environment.'.yml')) {
      $envs = \yaml_parse($this->fs->getContents($file_environment.'.yml'), true)[0];
    }
    return $envs ?? null;
  }

