function($variant = NULL, $start = NULL)
  {
    if ($this->check()) {
      if (!$variant) {
        $variant = 'default';
      }
      if (!($real_start = $this->db->select_one('bbn_statistics', 'MAX(day)', ['id_option' => $this->id_option, 'code' => $variant]))) {
        if ($start) {
          $real_start = $start;
        }
        elseif (!empty($this->ocfg['start'])) {
          $real_start = $this->ocfg['start'];
        }
        else {
          $real_start = self::ODATE;
        }
      }
      if (\bbn\str::is_date_sql($real_start)) {
        $num_days = 0;
        $num = $this->db->count('bbn_statistics', ['id_option' => $this->id_option, 'code' => $variant]);
        $today = date('Ymd');
        $last_res = null;
        $last_date = $real_start;
        $time = mktime(12, 0, 0, (int)substr($real_start, 5, 2), (int)substr($real_start, 8, 2), (int)substr($real_start, 0, 4));
        $test = date('Ymd', $time);
        while ($test <= $today) {
          $num_days++;
          $res = $this->run($real_start);
          if (!$res) {
            $res = 0;
          }
          if (($res !== $last_res) || !$num) {
            if ($this->db->count('bbn_statistics', [
              'id_option' => $this->ocfg['id'],
              'code' => $variant,
              'day' => $real_start
            ])) {
              $this->db->update('bbn_statistics', [
                'res' => $res
              ], [
                'id_option' => $this->ocfg['id'],
                'code' => $variant,
                'day' => $real_start
              ]);
            }
            else {
              $this->db->insert('bbn_statistics', [
                'id_option' => $this->ocfg['id'],
                'code' => $variant,
                'day' => $real_start,
                'res' => $res
              ]);
            }
            $last_res = $res;
            $num++;
          }
          else {
            $this->db->update('bbn_statistics', [
              'day' => $real_start
            ], [
              'id_option' => $this->ocfg['id'],
              'code' => $variant,
              'day' => $last_date
            ]);
          }
          $last_date = $real_start;
          $time += 24*3600;
          $real_start = date('Y-m-d', $time);
          $test = date('Ymd', $time);
        }
        return $num_days;
      }
    }
    return null;
  }

serie.code:
function($values = 30, $start = NULL, $end = NULL)
  {
    if ($this->ocfg) {
      if (!$end) {
        $end = date('Y-m-d');
      }
      if (!$start) {
        $start = date('Y-m-d', strtotime($end.' 12:00:00') - ($values*24*3600));
      }
      if (bbn\str::is_date_sql($start, $end)) {
        $res = [
          'labels' => [],
          'series' => []
        ];
        // All the values between start and end
        $all = $this->db->rselect_all('bbn_statistics', ['day', 'res'], [
          [
            'field' => 'id_option',
            'value' => $this->id_option
          ], [
            'field' => 'day',
            'operator' => '>=',
            'value' => $start
          ], [
            'field' => 'day',
            'operator' => '<=',
            'value' => $end
          ]
        ], [
          'day' => 'ASC'
        ]);
        // As the row appears with its last date for value we pick the nextr row if the last row is not the last day
        $last = count($all) - 1;
        if (($all[$last]['day'] !== $end) && ($tmp = $this->db->rselect('bbn_statistics', ['day', 'res'], [
          [
            'field' => 'id_option',
            'value' => $this->id_option
          ], [
            'field' => 'day',
            'operator' => '>',
            'value' => $end
          ]
        ], [
          'day' => 'ASC'
        ]))) {
          $all[] = $tmp;
          $last++;
        }
        if (count($all)) {
          $dcurrent = new \DateTime($start);
          $dend = new \DateTime($end);
          $num_days = (int)$dend->diff($dcurrent)->format('%a');
          $diff = $num_days;
          $interval = (int)floor(($num_days+1) / $values);
          $num = 0;
          $idx = 0;
          $didx = 0;
          $pstart = false;
          while ($diff >= 0) {
            $current = $dcurrent->format('Y-m-d');
            if (!$pstart) {
              $pstart = $current;
            }
            if ($num === $interval) {
              $num = 0;
            }
            if (!$num) {
              if (!empty($this->ocfg['total']) || ($interval === 1)) {
                $res['labels'][$didx] = $current;
              }
              else {
                $res['labels'][$didx] = bbn\date::format($pstart, 's').' - '.bbn\date::format($current, 's');
              }
              if (isset($res['series'][$didx]) && empty($this->ocfg['total'])) {
                $res['series'][$didx] = $res['series'][$didx] + $all[$idx]['res'];
              }
              else {
                $res['series'][$didx] = $all[$idx]['res'];
              }
              $pstart = false;
              $didx++;
            }
            elseif (empty($this->ocfg['total'])) {
              $res['labels'][$didx] = $current;
              $res['series'][$didx] += (int)$all[$idx]['res'];
            }
            if (!$diff) {
              break;
            }
            if ($current === $all[$idx]['day']) {
              $idx++;
            }
            $dcurrent = $dcurrent->add(date_interval_create_from_date_string('1 days'));
            $diff = (int)$dend->diff($dcurrent)->format('%a');
            $num++;
          }
        }
        return $res;
      }
    }
    return null;
  }

