function(array $cfg, $error = false)
  {
    $fs = new bbn\file\system();
    if (($path = $this->get_log_path($cfg, $error, true)) && $fs->is_dir($path)) {
      $fs->cd($path);
      $dirs = array_reverse($fs->get_files('./', true, true, null, 'cts'));
      $tmp =& $dirs;
      $tpath = '';
      for ($i = 0; $i < 4; $i++) {
        if (!count($tmp)) {
          break;
        }
        else{
          $ok = false;
          foreach ($tmp as &$t) {
            if (!empty($t['num'])) {
              $tpath .= '/'.$t['name'];
              $fs->cd($path.$tpath);
              $t['expanded'] = true;
              $t['items'] = array_reverse($fs->get_files('./', true, true, null, 'cts'));
              $tmp =& $t['items'];
              $ok = true;
              break;
            }
          }
          if (!$ok) {
            break;
          }
        }
      }
      return $dirs;
    }
  }

get_last_logs.code:
function(array $cfg, $error = false, $start = 0, $num = 10)
  {
    $fs = new \bbn\file\system();
    if (($path = $this->get_log_path($cfg, $error, true)) && $fs->is_dir($path)) {
      $res = [];
      $fs->cd($path);
      $years = array_reverse($fs->get_dirs($path));
      foreach ($years as $y) {
        $months = array_reverse($fs->get_dirs($y));
        foreach ($months as $m) {
          $days = array_reverse($fs->get_dirs($m));
          foreach ($days as $d) {
            $nums = array_reverse($fs->get_dirs($d));
            foreach ($nums as $num) {
              foreach (array_reverse($fs->get_files($num)) as $f) {
                if ($start) {
                  $start--;
                }
                if (!$start) {
                  $res[] = $f;
                  if (count($res) >= $num) {
                    return $res;
                  }
                }
              }
            }
          }
        }
      }
      return $res;
    }
    return null;
  }

