get_by_columns.code:
function()
  {
    if ( !$this->write ){
      $r = [];
			$ds = $this->fetchAll(\PDO::FETCH_ASSOC);
			foreach ( $ds as $d ){
				foreach ( $d as $k => $v ){
					if ( !isset($r[$k]) ){
						$r[$k] = [];
          }
					$r[$k][] = $v;
				}
			}
      return $r;
    }
    return null;
  }

