  }

get_tree.code:
function($dir, $only_dir = false, $filter = NULL, $hidden = false)
  {
    $r = [];
    $dir = self::clean($dir);
    $dirs = self::get_dirs($dir, $hidden);
    if ( \is_array($dirs) ){
      foreach ( $dirs as $d ){
        $x = [
          'name' => $d,
          'type' => 'dir',
          'num_children' => 0,
          'items' => self::get_tree($d, $only_dir, $filter, $hidden)
        ];
        $x['num_children'] = \count($x['items']);
        if ( $filter ){
          if ( $filter($x) ){
            $r[] = $x;
          }
        }
        else{
          $r[] = $x;
        }
      }
      if ( !$only_dir ){
        $files = self::get_files($dir, false, $hidden);
        foreach ( $files as $f ){
          $x = [
            'name' => $f,
            'type' => 'file',
            'ext' => bbn\str::file_ext($f)
          ];
          if ( $filter ){
            if ( $filter($x) ){
              $r[] = $x;
            }
          }
          else{
            $r[] = $x;
          }
        }
      }
    }
    return $r;
  }

create_path.code:
function($dir, $chmod = false)
  {
      return false;
    }
    clearstatcache();
    if ( !is_dir($dir) ){
      $bits = explode('/', $dir);
      $path = empty($bits[0]) ? '/' : '';
      foreach ( $bits as $i => $b ){
        if ( !empty($b) ){
          $path .= $b;
          if ( !is_dir($path) ){
            if ( !mkdir($path) || !is_dir($path) ){
              return false;
            }
            if ( $chmod ){
              @chmod($path, $chmod);
            }
          }
          $path .= '/';
        }
      }
    }
    return $dir;
  }

move.code:
function($orig, $dest, $st = '_v', $length = 0)
  {
    if ( file_exists($orig) && self::create_path(\dirname($dest)) ){
      if ( file_exists($dest) ){
        if ( $st === true ){
          self::delete($dest);
        }
        else{
          $i = 1;
          while ( $i ){
            $dir = \dirname($dest).'/';
            $file_name = bbn\str::file_ext($dest, 1);
            $file = $file_name[0].$st;
            if ( $length > 0 ){
              $len = \strlen(bbn\str::cast($i));
              if ( $len > $length ){
                return false;
              }
              $file .= str_repeat('0', $length - $len);
            }
            $file .= bbn\str::cast($i);
            if ( !empty($file_name[1]) ){
              $file .= '.'.$file_name[1];
            }
            $i++;
            if ( !file_exists($dir.$file) ){
              $dest = $dir.$file;
              $i = false;
            }
          }
        }
      }
      if ( rename($orig, $dest) ){
        return true;
      }
    }
    return false;
  }

copy.code:
function($src, $dst)
  {
    if ( is_file($src) ){
      return copy($src, $dst);
    }
    if ( is_dir($src) && self::create_path($dst) ){
      $files = self::get_files($src);
      $dirs = self::get_dirs($src);
      foreach ( $files as $f ){
        copy($f, $dst.'/'.basename($f));
      }
      foreach ( $dirs as $f ){
        self::copy($f, $dst.'/'.basename($f));
      }
      return true;
    }
    else{
      return false;
    }
  }

