function($path)
  {
    if ($this->check()) {
      while ( strpos($path, '../') ===  0 ){
        $tmp = dirname($this->current);
        if ( $tmp !== $this->current ){
          $path = substr($path, 3);
        }
        else {
          break;
        }
      }
      if ( isset($tmp) ){
        $path = $tmp.$path;
      }
      if (($p = $this->get_real_path($path)) && \is_dir($p)) {
        $this->current = $this->clean_path($path);
        return true;
      }
    }
    return false;
  }

