function($path, $hidden_is_empty = false)
  {
    $res = [];
    if ( $this->is_dir($path) ){
      foreach ( $this->get_dirs($path) as $d ){
        if ( $rs = $this->_get_empty_dirs($this->get_real_path($d), $hidden_is_empty) ){
          foreach ( $rs as $r ){
            $res[] = $this->get_system_path($r);
          }
        }
      }
    }
    return $res;
  }

