function()
  {
    if ($this->_path) {
      $p = dirname($this->_path);
      if ($p === '.') {
        return '';
      }
      if (($prepath = $this->get_prepath()) 
          && (strpos($p, $prepath) === 0)
      ) {
        return substr($p, \strlen($prepath));
      }
      return $p;
    }
    return null;
  }

