        ( empty($data) ? '' : 'var data = '.\bbn\x::js_object($data).';' ).
        $r.
        //( $encapsulated ? '})(jQuery);' : '' ).
        ( $encapsulated ? '})();' : '' ).
        '</script>';
    }
    return false;
  }

