function()
  {
    $args = \func_get_args();
    $has_path = false;
    foreach ($args as $i => $a){
      if ($new_data = $this->retrieve_var($a)) {
        $this->js_data($new_data);
      }
      elseif (\is_string($a)) {
        $has_path = 1;
      }
      elseif (\is_array($a)) {
        $this->js_data($a);
      }
      elseif ($a === true) {
        $this->js_data($this->data);
      }
    }
    if (!$has_path) {
      array_unshift($args, $this->_path);
    }
    $args[] = 'js';
    if ($r = $this->get_view(...$args)) {
      $this->add_script($r);
    }
    return $this;
  }

