function($plugin_path)
  {
    spl_autoload_register(function ($class_name) use ($plugin_path){
      if (
        (strpos($class_name,'/') === false) &&
        (strpos($class_name,'.') === false)
      ){
        $cls = explode('\\', $class_name);
        $path = implode('/', $cls);
        if ( file_exists($plugin_path.'lib/'.$path.'.php') ){
          include_once($plugin_path.'lib/'.$path.'.php');
        }
      }
    });
    return $this;
  }

