get_component.code:
function($name, array $data = [])
  {
    if ( $tmp = $this->route_component($name) ){
      if ( !empty($tmp['js']) ){
        $v = new view($tmp['js']);
        $res = [
          'script' => $v->get($data)
        ];
        if ( !empty($tmp['css']) ){
          $v = new view($tmp['css']);
          $res['css'] = $v->get();
        }
        if ( !empty($tmp['html']) ){
          $v = new view($tmp['html']);
          $res['content'] = $v->get($data);
        }
        return $res;
      }
    }
    return null;
  }

