get_model.code:
function()
  {
    $die = false;
    foreach ( $args as $a ){
      if ( \is_string($a) ){
        $path = $a;
      }
      else if ( \is_array($a) ){
        $data = $a;
      }
      else if ( \is_bool($a) ){
        $die = $a;
      }
    }
    if ( empty($path) ){
      $path = $this->_path;
      if ( ($this->get_mode() === 'dom') && (!defined('BBN_DEFAULT_MODE') || (BBN_DEFAULT_MODE !== 'dom')) ){
        $path .= '/index';
      }
    }
    else if ( strpos($path, './') === 0 ){
      $path = $this->get_current_dir().substr($path, 1);
    }
    if ( !isset($data) ){
      $data = $this->data;
    }
    $m = $this->_mvc->get_model($path, $data, $this);
    if ( \is_object($m) ){
      $m = bbn\x::to_array($m);
    }
    if ( !\is_array($m) ){
      if ( $die ){
        die("$path is an invalid model");
      }
      return [];
    }
    return $m;
  }

