is_controlled_by.code:
function($path, $type = 'public')
  {
    if ($this->_ctrl && ($this->_ctrl->get_path() === $path)) {
      if ($type === 'cli') {
        return $this->mvc->is_cli();
      }
      if ($type === $this->_ctrl->mode) {
        return true;
      }
    }
    return false;
  }

