      $this->apply_locale($this->_plugin);
    }
    if ( $this->_checkers ){
      foreach ( $this->_checkers as $chk ){
        $d = bbn\mvc::include_model($chk, $this);
        if (is_array($d)) {
          $this->add_data($d);
        }
      }
