    return bbn\mvc::include_model($this->_file, $this);
  }

has_data.code:
function($idx = NULL, $check_empty = false)
  {
    if ( !\is_array($this->data) ){
      return false;
    }
    if ( \is_null($idx) ){
      return !empty($this->data);
    }
    return \bbn\x::has_props($this->data, (array)$idx, $check_empty);
  }

