is_controlled_by.code:
function($path, $type = 'public')
  {
    if ($this->_ctrl && ($this->_ctrl->getPath() === $path)) {
      if ($type === 'cli') {
        return $this->mvc->isCli();
      }
      if ($type === $this->_ctrl->mode) {
        return true;
      }
    }
    return false;
  }

