has_data.code:
function($idx = NULL, $check_empty = false)
  {
    if ( !\is_array($this->data) ){
      return false;
    }
    if ( \is_null($idx) ){
      return !empty($this->data);
    }
    return \bbn\X::hasProps($this->data, (array)$idx, $check_empty);
  }

add_data.code:
function(array $data)
  {
		$ar = \func_get_args();
		foreach ( $ar as $d ){
			if ( \is_array($d) ){
				$this->data = $this->hasData() ? array_merge($this->data,$d) : $d;
			}
		}
		return $this;
  }

