update_info.code:
function(array $d)
  {
    if ( $this->check_session() ){
      $update = [];
      foreach ( $d as $key => $val ){
        if (
          ($key !== $this->fields['id']) &&
          ($key !== $this->fields['cfg']) &&
          ($key !== 'auth') &&
          ($key !== 'pass') &&
          \in_array($key, $this->fields)
        ){
          $update[$key] = $val;
        }
      }
      if ( \count($update) > 0 ){
        $r = (bool)$this->db->update(
                $this->class_cfg['tables']['users'],
                $update,
                [$this->fields['id'] => $this->id]);
        /** @todo Why did I do this?? */
        if ( $r ){
          $this->set_session(['cfg' => false]);
          $this->_user_info();
          return $r;
        }
      }
    }
    return false;
  }

