      }
      else{
        return null;
      }
    }

    if (count($p) > 1) {
      if ($p[0] === 'page') {
        array_shift($p);
      }
      else{
        //$p[0] = bbn\mvc::
      }

      return bbn\x::join($p, '');
    }

    return null;
  }

