function($id_option = NULL, $type = 'page')
  {
    if (($id_option = $this->_get_id_option($id_option, $type))
        && ($os = $this->opt->options(\func_get_args()))
    ) {
      $res = [];
      foreach ($os as $o){
        if ($this->pref->has($o['id'])) {
          $res[] = $o;
        }
      }

      return $res;
    }

    return null;
  }

