has.code:
function($id_option = NULL, $type = 'page', $force = false)
  {
    if (!$force && $this->user && $this->user->is_dev()) {
      return true;
    }

    if ($id_option = $this->_get_id_option($id_option, $type)) {
      $option = $this->opt->option($id_option);
      if (!empty($option['public'])) {
        return true;
      }

      return $this->pref->has($id_option, $force);
    }

    return false;
  }

has_deep.code:
function($id_option = NULL, $type = 'page', $force = false)
  {
    if (!$force && $this->user && $this->user->is_dev()) {
      return true;
    }

    if ($this->has($id_option, $type, $force)) {
      return true;
    }

    if (($id_option = $this->_get_id_option($id_option, $type))
        && ($options = $this->opt->full_options($id_option))
    ) {
      foreach ($options as $option){
        if ($this->has_deep($option['id'], $type, $force)) {
          return true;
        }
      }
    }

    return false;
  }

customize.code:
function(array $arr)
  {
    $res = [];
    if (isset($arr[0])) {
      foreach ($arr as $a){
        if (isset($a['id']) && $this->has($a['id'])) {
          $res[] = $a;
        }
      }
    }
    elseif (isset($arr['items'])) {
      $res = $arr;
      unset($res['items']);
      foreach ($arr['items'] as $a){
        if (isset($a['id']) && $this->has($a['id'])) {
          if (!isset($res['items'])) {
            $res['items'] = [];
          }

          $res['items'][] = $a;
        }
      }
    }

    return $res;
  }

