function($path, $format = 'Y/m/d', $max = 100, bbn\file\system $fs = NULL)
  {
    if ( empty($format) ){
      $format = 'Y/m/d';
    }
    if ( !$max ){
      $max = 100;
    }
    if ( !$fs ){
      $fs = new file\system();
    }
    // One dir per $format
    $spath = date($format);
    if ( $spath ){
      $path = $fs->create_path($path.(substr($path, -1) === '/' ? '' : '/').$spath);
      if ( $fs->is_dir($path) ) {
        $num = count($fs->get_dirs($path));
        if ($num) {
          $num_files = count($fs->get_files($path.'/'.$num));
          if ($num_files >= $max){
            $num++;
          }
        }
        else {
          $num = 1;
        }
        if ( $fs->create_path($path.'/'.$num) ){
          return $path.'/'.$num.'/';
        }

      }
    }
    return null;
  }

