function($path, $format = 'Y/m/d', bbn\file\system $fs = NULL)
  {
    if ( empty($format) ){
      $format = 'Y/m/d';
    }
    if ( !$fs ){
      $fs = new file\system();
    }
    if (!$fs->is_dir($path)) {
      return null;
    }
    $limit = count(self::split($format, '/')) + 1;
    $res = 0;
    while ($limit > 0) {
      if (!$fs->get_num_files($path) && $fs->delete($path)) {
        $limit--;
        $res++;
        $path = dirname($path);
      }
      else{
        break;
      }
    }
    return $res;
  }

