function($obj)
  {
    $replace_keys = [];

    //$obj = \bbn\x::convert_uids($obj);
    $transform = function($o, $idx = 0) use(&$transform, &$value_arr, &$replace_keys){
      foreach( $o as $key => &$value ){
        $idx++;
        if ( \is_array($value) || \is_object($value) ){
          $value = $transform($value, $idx);
        }
        else if (
          \is_string($value) &&
          // Look for values starting with 'function('
          (strpos(trim($value), 'function(') === 0)
        ){
          // Store function string.
          $value_arr[] = $value;
          // Replace function string in $foo with a ‘unique’ special key.
          $value = "%bbn%$key%bbn%$idx%bbn%";
          // Later on, we’ll look for the value, and replace it.
          $replace_keys[] = '"'.$value.'"';
        }
      }
      return $o;
    };
    // Now encode the array to json format
    $json = json_encode($transform($obj));
    /* $json looks like:
    {
      “number”:1,
      “float”:1.5,
      “array”:[1,2],
      “string”:”bar”,
      “function”:”%bbn%function%bbn%5%bbn%”
    }
    */
    // Replace the special keys with the original string.
    return \count($replace_keys) ? str_replace($replace_keys, $value_arr, $json) : $json;
  }

indent_json.code:
function($json)
  {
    $result      = '';
    $pos         = 0;
    $strLen      = \strlen($json);
    $indentStr   = '  ';
    $newLine     = "\n";
    $prevChar    = '';
    $outOfQuotes = true;

    for ($i=0; $i<=$strLen; $i++){

      // Grab the next character in the string.
      $char = substr($json, $i, 1);

      // Are we inside a quoted string?
      if ($char == '"' && $prevChar != '\\'){
        $outOfQuotes = !$outOfQuotes;

        // If this character is the end of an element,
        // output a new line and indent the next line.
      } else if(($char == '}' || $char == ']') && $outOfQuotes){
        $result .= $newLine;
        $pos --;
        for ($j=0; $j<$pos; $j++){
          $result .= $indentStr;
        }
      }

      // Add the character to the result string.
      $result .= $char;

      // If the last character was the beginning of an element,
      // output a new line and indent the next line.
      if (($char == ',' || $char == '{' || $char == '[') && $outOfQuotes){
        $result .= $newLine;
        if ($char == '{' || $char == '['){
          $pos ++;
        }

        for ($j = 0; $j < $pos; $j++){
          $result .= $indentStr;
        }
      }

      $prevChar = $char;
    }

    return $result;
  }

