        $r = 'null';
      }
      else if ( $a === false ){
        $r = 'false';
      }
      else if ( $a === true ){
        $r = 'true';
      }
      else if ( $a === 0 ){
        $r = '0';
      }
      else if ( $a === '' ){
        $r = '""';
      }
      else if ( $a === [] ){
        $r = '[]';
      }
      else if ( !$a ){
        $r = '0';
      }
      else if ( !\is_string($a) && \is_callable($a) ){
        $r = 'Function';
      }
      else if ( \is_object($a) ){
        $n = \get_class($a);
        if ( $n === 'stdClass' ){
          $r = str::export($a);
        }
        else{
          $r = $n.' Object';
        }
      }
      else if ( \is_array($a) ){
        $r = str::export($a);
      }
      else if ( \is_resource($a) ){
        $r = 'Resource '.get_resource_type($a);
      }
      else if ( str::is_buid($a) ){
        $r = '0x'.bin2hex($a);
      }
      $st .= $r.PHP_EOL;
    }
    return PHP_EOL.$st;
  }

